import React, { useState, useEffect } from 'react';
import './todoliststyle.css';

export default function TodoList() {
    const [tasks, setTasks] = useState([]);
    const [taskText, setTaskText] = useState("");

    useEffect(() => {
        const storedTasks = localStorage.getItem('tasks');
        if (storedTasks) {
            setTasks(JSON.parse(storedTasks));
        }
    }, []);

    const saveTasksToLocalStorage = (tasks) => {
        localStorage.setItem('tasks', JSON.stringify(tasks));
    };

    const handleAddTask = () => {
        if (taskText.trim() === '') {
            return;
        }

        const newTask = {
            id: Date.now(),
            text: taskText,
        };

        const updatedTasks = [...tasks, newTask];
        setTasks(updatedTasks);
        saveTasksToLocalStorage(updatedTasks);
        setTaskText('');
    };

    const handleDeleteTask = (taskId) => {
        const updatedTasks = tasks.filter((task) => task.id !== taskId);
        setTasks(updatedTasks);
        saveTasksToLocalStorage(updatedTasks);
    };

    const containerStyle = {
        backgroundColor: 'purple',
        color: 'white',
        padding: '20px',
        borderRadius: '10px', // Add rounded corners
    };

    const addButtonStyle = {
        backgroundColor: 'purple',
        color: 'white',
        borderRadius: '5px', // Add rounded corners to the button
    };

    const taskListStyle = {
        listStyleType: 'none', // Remove bullet points
        padding: '0',
    };

    const taskItemStyle = {
        backgroundColor: 'pink', // Set pink background for task items
        padding: '10px',
        marginBottom: '10px',
        borderRadius: '5px', // Add rounded corners to task items
        display: 'flex',
        justifyContent: 'space-between',
        alignItems: 'center',
    };

    const deleteButtonStyle = {
        backgroundColor: 'purple',
        color: 'white',
        border: 'none',
        borderRadius: '5px', // Add rounded corners to the delete button
        cursor: 'pointer',
    };

    return (
        <div className="todo-container" style={containerStyle}>
            <h2 style={{ color: 'white' }}>Tasks</h2>
            <ul className="task-list" style={taskListStyle}>
                {tasks.map((task) => (
                    <li key={task.id} style={taskItemStyle}>
                        {task.text}
                        <button
                            onClick={() => handleDeleteTask(task.id)}
                            style={deleteButtonStyle}
                        >
                            Delete
                        </button>
                    </li>
                ))}
            </ul>
            <input
                type="text"
                value={taskText}
                onChange={(e) => setTaskText(e.target.value)}
                className="task-input"
                style={{ backgroundColor: 'pink', borderRadius: '5px' }}
            />
            <button onClick={handleAddTask} className="add-button" style={addButtonStyle}>
                Add Task
            </button>
        </div>
    );
}
