import React, { useState, useEffect } from 'react';
import './todoliststyle.css';

export default function TodoList() {
    const [tasks, setTasks] = useState([]);
    const [taskText, setTaskText] = useState("");

    useEffect(() => {
        const storedTasks = localStorage.getItem('tasks');
        if (storedTasks) {
            setTasks(JSON.parse(storedTasks));
        }
    }, []);

    const saveTasksToLocalStorage = (tasks) => {
        localStorage.setItem('tasks', JSON.stringify(tasks));
    };

    const handleAddTask = () => {
        if (taskText.trim() === '') {
            return;
        }

        const newTask = {
            id: Date.now(),
            text: taskText,
        };

        const updatedTasks = [...tasks, newTask];
        setTasks(updatedTasks);
        saveTasksToLocalStorage(updatedTasks);
        setTaskText('');
    };

    const handleDeleteTask = (taskId) => {
        const updatedTasks = tasks.filter((task) => task.id !== taskId);
        setTasks(updatedTasks);
        saveTasksToLocalStorage(updatedTasks);
    };

    const containerStyle = {
        backgroundColor: 'purple', // Change the background color to purple
        color: 'white', // Change the text color to white
        padding: '20px', // Add some padding
    };

    const addButtonStyle = {
        backgroundColor: 'purple', // Change the button color to purple
        color: 'white', // Change the button text color to white
    };

    return (
        <div className="todo-container" style={containerStyle}>
            <h2 style={{ color: 'white' }}>Tasks</h2>
            <ul className="task-list">
                {tasks.map((task) => (
                    <li key={task.id}>
                        {task.text}
                        <button onClick={() => handleDeleteTask(task.id)}>Delete</button>
                    </li>
                ))}
            </ul>
            <input
                type="text"
                value={taskText}
                onChange={(e) => setTaskText(e.target.value)}
                className="task-input"
            />
            <button onClick={handleAddTask} className="add-button" style={addButtonStyle}>
                Add Task
            </button>
        </div>
    );
}
